import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './gallery.css';

const Gallery = () => {
  const [imageUrls, setImageUrls] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [uploadError, setUploadError] = useState(null);
  const [uploadSuccess, setUploadSuccess] = useState(null);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/images/');
        setImageUrls(response.data.urls);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchImages();
  }, []);

  const handleUpload = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('name', event.target.name.value);
    formData.append('file', event.target.file.files[0]);

    // Log FormData content
    for (let pair of formData.entries()) {
      console.log(pair[0] + ': ' + pair[1]);
    }

    try {
      const response = await axios.post('/images/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setUploadSuccess(`Image uploaded successfully: ${response.data.url}`);
      setUploadError(null);
      setImageUrls((prevUrls) => [...prevUrls, response.data.url]);
    } catch (err) {
      console.error('Upload error:', err.response || err.message);
      const errorMessage = err.response?.data?.detail || err.message;
      setUploadError(Array.isArray(errorMessage) ? errorMessage.map(e => e.msg).join(', ') : errorMessage);
      setUploadSuccess(null);
    }
  };

  return (
    <div className="Gallery">
      <form onSubmit={handleUpload}>
        <div>
          <label htmlFor="name">Image Name:</label>
          <input type="text" id="name" name="name" required />
        </div>
        <div>
          <label htmlFor="file">Upload Image:</label>
          <input type="file" id="file" name="file" accept="image/*" required />
        </div>
        <button type="submit">Upload</button>
      </form>
      {uploadError && <p>Error: {uploadError}</p>}
      {uploadSuccess && <p>{uploadSuccess}</p>}
    <div className="Gallery">
      {imageUrls.map((url, index) => (
        <img key={index} src={url.startsWith('http') ? url : `http://${url}`} alt={`Image ${index + 1}`} />
      ))}
    </div>
    </div>
  );
};


export default Gallery;
